#!/usr/bin/env bash
set -euo pipefail

ROOT="$(git rev-parse --show-toplevel)"

VAGRANT_DIR="$ROOT/infra/vagrant"
INV="$ROOT/infra/inventories/vagrant_local.ini"           # <- local inventory
PLAYBOOK="$ROOT/ansible/vms/playbooks/site.yml"
SSHCFG="$ROOT/infra/ssh/ssh.config"

# ‚úÖ Local Ansible config (uses ssh.config generated by vagrant ssh-config)
export ANSIBLE_CONFIG="$ROOT/infra/ansible/ansible-local.cfg"

# ‚úÖ Safety: force remote tmp used by Ansible on targets
export ANSIBLE_REMOTE_TEMP="/tmp/.ansible-local/tmp"
export ANSIBLE_HOST_KEY_CHECKING="False"

mkdir -p "$ROOT/infra/ssh"

TARGETS=(backend db front)
TARGET_PATTERN="backend:db:front"

# 1) Vagrant operations must run in the Vagrantfile directory
cd "$VAGRANT_DIR"

need_up=0
for m in "${TARGETS[@]}"; do
  state="$(vagrant status "$m" --machine-readable | awk -F, '$3=="state" {print $4}' | tail -n1 || true)"
  if [ "$state" != "running" ]; then
    need_up=1
  fi
done

if [ "$need_up" -eq 1 ]; then
  echo "üîß Bringing up: ${TARGETS[*]}"
  vagrant up "${TARGETS[@]}"
else
  echo "‚úÖ VMs already running: ${TARGETS[*]}"
fi

echo "üîÅ Generating $SSHCFG from vagrant for: ${TARGETS[*]}"
vagrant ssh-config backend db front dockerhost k8shost jenkins > "$SSHCFG"

# 2) Ansible should run from repo root so relative paths in inventory work
cd "$ROOT"

echo "‚ÑπÔ∏è Using ANSIBLE_CONFIG=$ANSIBLE_CONFIG"
ansible --version | head -n 5

echo "üß™ Ansible ping (targets only)..."
ansible -i "$INV" "$TARGET_PATTERN" -m ping

echo "üß∞ Ensure remote tmp exists on targets..."
ansible -i "$INV" "$TARGET_PATTERN" -b -m file -a "path=$ANSIBLE_REMOTE_TEMP state=directory mode=1777"

echo "üöÄ Deploy (targets only)..."
ansible-playbook -i "$INV" "$PLAYBOOK" --limit "$TARGET_PATTERN"
